import React, { useState, useEffect } from "react";

import Navbar from '../sub/Navbar';

import CategoryCreate from '../sub/menu/CategoryCreate';
import MenuCreate from '../sub/menu/MenuCreate';
import CategoryItems from '../sub/menu/CategoryItems';
//import MenuItems from '../sub/menu/MenuItems';

import { loadMenuAsync } from "../../slice/menuSlice";
import { useDispatch } from "react-redux";

import '../../styles/main/Menu.css';

const Menu = () => {

    const [visibleCreate, setVisibleCreate] = useState({
        visible: false,
        type: null
    });
    const dispatch = useDispatch();

    /**
     * @function (01) fetch the current menu on the initial load
     */
    useEffect(() => {
        //dispatch(loadMenuAsync);
    }, [])

    /**
     * @function (01)
     */
    const showCategory = () => {
        setVisibleCreate({...visibleCreate, type: 'category', visible: true})
    }
    /**
     * @function (01)
     */
    const showMenu = () => {
        setVisibleCreate({...visibleCreate, type: 'menu', visible: true})
    }
    /**
     * @function (01) pass this function to the child component and update the state
     */
    let hideCreateView = () => {
        setVisibleCreate({...visibleCreate, visible: false})
    }

    return (
        <div className="center_container">
            <Navbar />
            <div className="main_content">
                <div className="main_outer_content"></div>
                <div className="main_center_content">
                    <div className="main_sub_content upper_sub_content">
                        <div className="sub_heading">
                            <span>Kategorien</span>
                            <div className="add_button" onClick={showCategory}>Neue Kategorie</div>
                        </div>
                        <CategoryItems />
                    </div>
                    <div className="main_sub_content">
                        <div className="sub_heading">
                            <span>Gerichte</span>
                            <div className="add_button" onClick={showMenu}>Neues Gericht</div>
                        </div>
                        {/*<MenuItems />*/}
                    </div>
                    {visibleCreate.visible && visibleCreate.type === 'category' && <CategoryCreate hide={hideCreateView} /> }
                    {visibleCreate.visible && visibleCreate.type === 'menu' && <MenuCreate hide={hideCreateView} /> }
                </div>
                <div className="main_outer_content"></div>
            </div>
        </div>
    );
};

export default Menu;
