import React, {useState, useRef, useEffect} from "react";
import { fromRange } from "xpath-range"
import { v4 as uuidv4 } from 'uuid';

import Navbar from "../../general/navbar/Navbar";

import '../../../../styles/main/coder/editor/editor.css';

const Editor = () => {

    const [selTxt, setSelTxt] = useState({})
    const [tempCodes, setTempCodes] = useState([])


    useEffect(() => {

    }, []);

    //todo security checks

    /**
     * @function (01) check that there is no overlapping regarding the codes
     *           (02) highlight the code
     *           (03) push into data structure (updates the code overview on the right side)
     */
    const addCode = () => {
        //1
        //let startOverlapCheck = tempCodes.filter(code => code.start == filter.name && code.address == filter.address)
        //let endOverlapCheck = tempCodes.filter(code => code.start == filter.name && code.address == filter.address)

        //02
        setSelectionRange(selTxt.start, selTxt.end)
        //highlight("yellow");

        //03
        setTempCodes(currentCodes => [...currentCodes, selTxt]);
    }

    /**
     * @function (01) find the array entry by code id
     *           (02) add code to this entry
     * @param e is the event
     * @param i is the index of the array element which is
     */
    const editCode = (e, i) => {



        console.log('property name: '+ e.target.name);
        let newArr = [...tempCodes]; // copying the old datas array
        newArr[i] = e.target.value; // replace e.target.value with whatever you want to change it to
        setTempCodes(newArr);


    }

    /**
     * @function
     */
    const removeTxt = () => {
        alert('remove')
    }

    /**
     * @function (01) get all selects and positions
     *           (02) if only cursor change takes place only hide the tooltip
     *           (03) reposition tooltip
     *           (04) set selected text state
     */
    const selector = (e) => {
        //01
        let select = window.getSelection(),
            selectRange = select.getRangeAt(0),
            selectPos = selectRange.getBoundingClientRect(),
            codeInput = document.getElementsByClassName('coder_input')[0],
            codeInputPos = codeInput.getBoundingClientRect(),
            tooltip = document.getElementById('tooltip_editor_wrapper');
        //02
        if (selectPos.width < 1) return tooltip.style.display = "none";
        //03
        tooltip.style.display = "inline";
        tooltip.style.top = (selectPos.top - codeInputPos.top - tooltip.offsetHeight - 10 + codeInput.scrollTop)+'px';
        tooltip.style.left = (selectPos.left - codeInputPos.left + selectPos.width/2 - tooltip.offsetWidth / 2)+'px';
        //04
        let xpath = fromRange(selectRange, codeInput)

        setSelTxt({...selTxt,  ...{
                val: selectRange.toString(),
                start: xpath.startOffset,
                end: xpath.endOffset,
                id: uuidv4()
            },
        })
    }

    /**
     *
     * @param start
     * @param end
     */
    function setSelectionRange(start, end) {

        let el = document.getElementsByClassName('coder_input')[0];

        var range = document.createRange();
        range.selectNodeContents(el);
        var textNodes = getTextNodesIn(el);
        var foundStart = false;
        var charCount = 0, endCharCount;

        for (var i = 0, textNode; textNode = textNodes[i++]; ) {
            endCharCount = charCount + textNode.length;
            if (!foundStart && start >= charCount && (start < endCharCount || (start == endCharCount && i <= textNodes.length))) {
                range.setStart(textNode, start - charCount);
                foundStart = true;
            }
            if (foundStart && end <= endCharCount) {
                range.setEnd(textNode, end - charCount);
                break;
            }
            charCount = endCharCount;
        }

        var sel = window.getSelection();
        sel.removeAllRanges();
        sel.addRange(range);

        //Helper functions

        function getTextNodesIn(node) {
            var textNodes = [];
            if (node.nodeType === 3) {
                textNodes.push(node);
            } else {
                var children = node.childNodes;
                for (var i = 0, len = children.length; i < len; ++i) {
                    textNodes.push.apply(textNodes, getTextNodesIn(children[i]));
                }
            }
            return textNodes;
        }

    }

    /**
     *
     * @param colour
     */
    function highlight(colour) {

        let range, sel;

        sel = window.getSelection();
        if (sel.rangeCount && sel.getRangeAt) {
            range = sel.getRangeAt(0);
        }
        document.designMode = "on";
        sel.removeAllRanges();
        sel.addRange(range);
        document.execCommand("BackColor", false, colour);
        document.designMode = "off";
    }



    return (
        <div className="center_container">
            <Navbar />
            <div className="main_invite_content">
                <div className="main_outer_content">Coder editor - alles erstmal offline bis gepublished wird</div>
                <div className="main_center_content">

                    <div className="coder_input" placeholder="Patientendokumentation" contentEditable="true" onSelect={selector}>
                        {txt}
                        <div id="tooltip_editor_wrapper">
                            <div className="tooltip_btn" onClick={addCode}>Code hinzufügen</div>
                            <div className="tooltip_btn" onClick={removeTxt}>Löschen</div>
                        </div>
                    </div>
                    <div className="code_display">
                        {tempCodes.map((code, i) => {
                            return <div key={i}>
                                {code.val}
                                <input placeholder="Code" onKeyUp={(e) => editCode(e, i)}/>
                            </div>;
                        })}
                    </div>
                </div>
                <div className="main_outer_content">
                    Codes exportieren / anzeigen
                    Hochladen
                </div>
            </div>
        </div>
    );
};

export default Editor;














const txt = "Die wichtigste Klassifikation für klinische Diagnosen ist die Internationale statistische Klassifikation der Krankheiten und verwandter Gesundheitsprobleme (International Statistical Classification of Diseases and Related Health Problems, ICD). Sie wird von der WHO (World Health Organization) herausgegeben und weltweit für die Diagnosendokumentation, Todesursachenstatistik und andere medizinische Statistiken verwendet.\n" +
    "\n" +
    "Erste Versuche mit einer Klassifikation von Krankheiten bzw. Todesursachen gab es bereits im 18. Jahrhundert. So hat der Probst Johann Peter Süßmilch (1707-1767) eine Todesursachenstatistik für Berlin mit mehr als 80.000 Fällen veröffentlicht. Im 19. Jahrhundert wurde versucht, die Kenntnis über die Häufigkeit verschiedener Todesursachen in soziale Maßnahmen einfließen zu lassen. 1893 wurde im Auftrag des Internationalen Statistischen Instituts von dem Direktor des statistischen Amtes von Paris, Dr. Jacques Bertillot, ein systematisches Todesursachen-Verzeichnis vorgelegt, in das Erfahrungen aus Frankreich, England, Deutschland und der Schweiz eingingen. Als Grundlage diente das Prinzip der Unterscheidung zwischen Allgemeinkrankheiten und lokalisierten Organerkrankungen. Bis 1938 gab es fünf Revisionen dieses Todesursachen-Verzeichnisses. Seit 1946 liegt die Verantwortung für die ICD bei der Weltgesundheitsorganisation WHO. Neben der Definition des Verzeichnisses (6. Revision) einigte man sich damals auch auf internationale Vorschriften zur Feststellung des Grundleidens und auf die Annahme eines umfassenden Programms der internationalen Zusammenarbeit auf dem Gebiet der Bevölkerungs- und Gesundheitsstatistik.\n" +
    "\n" +
    "Nach umfangreichen Vorarbeiten zu Inhalt, Aufbau und Verwendungszweck der ICD wurde 1987 die 10. Revision (ICD-10) von der WHO-Vollversammlung verabschiedet. Diese Version wird weltweit für die Gesundheitsstatistik verwendet und erlaubt damit Vergleiche zwischen einzelnen Ländern. Die deutsche Fassung wird von dem Deutschen Institut für Medizinische Dokumentation und Information (DIMDI) in Köln herausgegeben. Für die Todesursachenstatistik wird die ICD-10 in Deutschland seit dem 1.1.1998 eingesetzt (ICD-10-WHO). Die aktuelle Fassung für die stationäre und vertragsärztliche ambulante Gesundheitsversorgung wird als Version ICD-10-GM (German Modification) bezeichnet. Sie enthält eine Reihe von Modifikationen für das deutsche Gesundheitssystem, insbesondere Anpassungen für die Abrechnung stationärer Leistungen mit dem diagnose-orientierten Fallpauschalensystem (DRG-System).\n" +
    "\n" +
    "Die ICD-10 ist als numerische (d.h. Gliederung nach Schlüsseln) und alphabetische (d.h. Gliederung nach Krankheitsbezeichnungen) Klassifikation zusammen mit den Regeln ihrer Anwendung im Buchhandel erhältlich. Die alphabetische Klassifikation wird auch als Diagnosenthesaurus bezeichnet. Die aktuelle Version der ICD-10 kann in beiden Formen auf der WWW-Seite des DIMDI gelesen werden."
