import React, { useState, useRef } from "react";

import {selectIsLoggedIn, selectUserType} from "../../../../slice/authSlice";
import { useDispatch, useSelector } from "react-redux";
import {history} from "../../../../helpers/history";

import Navbar from "../../general/Navbar";

import '../../../../styles/main/admin/invite/InviteUser.css';

const InviteUser = () => {

    const loggedIn = useSelector(selectIsLoggedIn)
    const userType = useSelector(selectUserType)

    if (!loggedIn || userType !== 'admin') {
        history.push("/login");
        window.location.reload();
    }

    const dispatch = useDispatch();

    const [inviteType, setInviteType] = useState('student');

    // ======================================

    /**
     * @function
     */
    const inviteUser = (e) => {
        e.preventDefault();
        alert("moin")
    }

    // ======================================

    return (
        <div className="center_container">
            <Navbar />
                <div className="main_content">
                    <div className="main_outer_content"></div>
                    <div className="main_center_content">
                        <form onSubmit={inviteUser}>

                            <input type="radio" name="foo" value="student" checked onChange={() => {setInviteType('student')}}/>
                            <input type="radio" name="foo" value="coder" onChange={() => {setInviteType('coder')}} />

                            <input placeholder="First name" name="first_name" required/>
                            <input placeholder="Last name" name="last_name" type="text" required/>
                            <input placeholder="E-Mail" name="email" type="email" required/>

                            {inviteType === 'student' && (
                                <input placeholder="Immatrikulationsnummer" name="imNumber" type="text" required/>
                            )}

                            <button type="submit">Einladen</button>


                        </form>
                    </div>
                    <div className="main_outer_content">

                    </div>
                </div>
        </div>
    );
};

export default InviteUser;
