import React, { useState, useEffect } from "react";

import {selectMenu, setMenu, selectCurrentCategory} from "../../../slice/menuSlice";
import { useDispatch, useSelector } from "react-redux";

import '../../../styles/sub/menu/MenuCreate.css';

const MenuCreate = ({ hide }) => {
    const menuList = useSelector(selectMenu)
    const categorySelected = useSelector(selectCurrentCategory)

    const dispatch = useDispatch();

    /**
     *
     */
    useEffect(() => {
        let bg = document.querySelector('.menu_dark_bg'),
            content = document.querySelector('.menu_input_wrapper');
        bg.style.cssText = "opacity: 100%";
        content.style.marginTop = "0";
    }, [])


    /**
     * @function (01) obtain value of input and check that not empty
     *           (02) use selector to check if category already exists
     *           (03) dispatch to menu reducer and add to data structure
     *           (04) hide the add category view again
     */
    const addMenu = (e) => {
        //01
        //const val = document.querySelector('.category_input').value;
        //if (!val) return alert('provide value');
        //02
        //if (menuList.filter(category => category.name.toLowerCase() === val.toLowerCase()).length !== 0) return alert('already exists');
        //03 todo set dish to a given category
        //dispatch(setMenu({name: val, rank: 1, dishes:[]}));
        //04
        hideMenuAddView(null, true)
    };

    /**
     * @function (01) get the background and content
     *           (02) return if user clicked on the content
     *           (03) user clicked outside of content - hide
     *           (04) after timeout set state again to hidden
     */
    const hideMenuAddView = (e, forced) => {
        //01
        let bg = document.querySelector('.menu_dark_bg'),
            content = document.querySelector('.menu_input_wrapper');
        //02
        if (!forced && content.contains(e.target)) return;
        //03
        bg.style.cssText = "opacity: 0%";
        content.style.marginTop = "150vh";
        //04
        setTimeout(() => hide(), 350)
    };

    return (
        <div className="menu_dark_bg" onClick={hideMenuAddView}>
            <div className="menu_input_wrapper">
                <div className="menu_input_hide" onClick={(e) => {hideMenuAddView(e, true)}}>X</div>
                <form onSubmit={addMenu}>
                    <input type="file" placeholder="Bild"/>
                    <input placeholder="Name"/>
                    <input placeholder="Beschreibung"/>
                    <input placeholder="Zutaten"/>
                    <input placeholder="Allergene"/>
                    <input placeholder="Zusatzstoffe"/>
                    <input placeholder="Preis"/>
                    <input placeholder="Extras Liste mit Preis"/>
                    <input placeholder="Angebot inkl Zeitraum und Preis"/>
                    <button className="category_submit">Hinzuf√ºgen</button>
                </form>
            </div>
        </div>
    );
};

export default MenuCreate;
