import React, {useState, useRef, useEffect} from "react";

import '../../styling/sub/OrderList.css';

const OrderList = ({socket, orderData}) => {

    //todo include the order data into the socket data -> merge both objects and then sort them

    const [orders, setOrders] = useState({});
    useEffect(() => {
        const orderListener = (message) => {
            setOrders((prevMessages) => {
                const newMessages = {...prevMessages};
                newMessages[message.id] = message;
                return newMessages;
            });
        };

        const deleteOrderListener = (messageID) => {
            setOrders((prevMessages) => {
                const newMessages = {...prevMessages};
                delete newMessages[messageID];
                return newMessages;
            });
        };

        socket.on('message', orderListener);
        socket.on('deleteMessage', deleteOrderListener);
        socket.emit('getMessages');

        return () => {
            socket.off('message', orderListener);
            socket.off('deleteMessage', deleteOrderListener);
        };
    }, [socket]);

    //Helper functions

    //todo order finished
    //todo order aborted

    return (
        <div className="order_list_container">
            {[...Object.values(orders)]
                .sort((a, b) => a.time - b.time)
                .map((order) => (
                    <div
                        key={order.id}
                        className="order_container"
                        title={`Sent at ${new Date(order.time).toLocaleTimeString()}`}
                    >
                        <span className="user">{order.user.name}:</span>
                        <span className="message">{order.value}</span>
                        <span className="date">{new Date(order.time).toLocaleTimeString()}</span>
                    </div>
                ))
            }
        </div>
    );
};

export default OrderList;



































