import React, { useState } from "react";
import { Redirect } from 'react-router-dom';
import {selectIsLoggedIn, selectMessage, selectUserType, loginAsync} from "../../slice/authSlice";

import { useDispatch, useSelector } from "react-redux";

import { history } from "../../helpers/history";

import '../../styles/Login.css';

const Login = () => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [forgotPsw, setForgotPsw] = useState(false);

    const [loading, setLoading] = useState(false);

    const isLoggedIn = useSelector(selectIsLoggedIn);
    const message = useSelector(selectMessage);

    const dispatch = useDispatch();

    //Change handlers
    const changeEmail = (e) => {
        setEmail(e.target.value);
    };
    const changePassword = (e) => {
        setPassword(e.target.value);
    };

    /**
     * @function (01)
     */
    const performLogin = (e) => {
        e.preventDefault();

        setLoading(true);

        //history.push(`/${userType}`);
        //window.location.reload();

        /*
        dispatch(loginAsync({email: email, password: password}))
            .then(() => {
                props.history.push("/profile");
                window.location.reload();
            })
            .catch(() => {
                setLoading(false);
            });
         */
    };

    /**
     * @function (01)
     */
    const performForgotPsw = (e) => {

    }

    //if (isLoggedIn) return <Redirect to="/profile" />;

    return (
        <div className="login_wrapper">
            <div className="login_section">
                {!forgotPsw ? (
                    <form onSubmit={performLogin}>
                        <input name="email" className="" placeholder="E-Mail" type="email" required onChange={changeEmail}/>
                        <input name="password" className="" placeholder="Password" type="password" required onChange={changePassword}/>
                        <button type="submit">
                            {loading && (<span>Processing</span>)}
                            <span>Login</span>
                        </button>
                        <div onClick={() => {setForgotPsw(true)}}>Forgot password</div>
                    </form>
                ) : (
                    <form onSubmit={performForgotPsw}>
                        <input name="email" className="" placeholder="E-Mail" type="email" required onChange={changeEmail}/>
                        <button type="submit">
                            {loading && (<span>Processing</span>)}
                            <span>Login</span>
                        </button>
                        <div onClick={() => {setForgotPsw(false)}}>Remember password</div>
                    </form>
                )}

                {message && (
                    <div className="" role="alert">
                        {message}
                    </div>
                )}
            </div>
            <div className="login_section">

            </div>
        </div>
    );
};

export default Login;
