import React, { useState, useRef } from "react";

import Navbar from '../sub/Navbar';
import CategoryCreate from '../sub/menu/CategoryCreate';
import MenuCreate from '../sub/menu/MenuCreate';

//import CategoryItem from '../sub/menu/CategoryItem';
//import MenuItem from '../sub/menu/MenuItem';

import '../../styles/main/Menu.css';

const Menu = () => {

    const [visibleCreate, setVisibleCreate] = useState({
        visible: false,
        type: null
    });

    /**
     * @function (01)
     */
    const showCategory = () => {
        setVisibleCreate({...visibleCreate, type: 'category', visible: true})
    }
    /**
     * @function (01)
     */
    let hideCategory = () => {
        setVisibleCreate({...visibleCreate, visible: false})
    }

    /**
     * @function (01)
     */
    const showMenu = () => {
        setVisibleCreate({...visibleCreate, type: 'menu', visible: true})
    }
    /**
     * @function (01)
     */
    const hideMenu = () => {
        alert('hide menu')
    }

    return (
        <div className="center_container">
            <Navbar />
            <div className="main_content">
                <div className="main_outer_content"></div>
                <div className="main_center_content">
                    <div className="main_sub_content upper_sub_content">
                        <div className="sub_heading">
                            <span>Kategorien</span>
                            <div className="add_button" onClick={showCategory}>Neue Kategorie</div>
                        </div>
                    </div>
                    <div className="main_sub_content">
                        <div className="sub_heading">
                            <span>Gerichte</span>
                            <div className="add_button" onClick={showMenu}>Neues Gericht</div>
                        </div>
                    </div>
                    {visibleCreate.visible && visibleCreate.type === 'category' && <CategoryCreate props={this.hideCategory} /> }
                    {visibleCreate.visible && visibleCreate.type === 'menu' && <MenuCreate props={this.hideMenu} /> }
                </div>
                <div className="main_outer_content"></div>
            </div>
        </div>
    );
};

export default Menu;
