import React, {useState, useRef, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

import Tooltip from "../../components/ind/Tooltip";

import '../../styling/sub/Navbar.css';

import logo from'../../assets/logo.svg';
import order from'../../assets/order.svg';
import income from'../../assets/income.svg';
import settings from'../../assets/settings.svg';
import rating from'../../assets/rating.svg';
import logout from'../../assets/logout.svg';
import promotions from'../../assets/promotions.svg';
import menu from'../../assets/menu.svg';
import analytics from'../../assets/analytics.svg';

const Navbar = () => {

    /**
     * @function (01) on render of the component get the current path depending on the path add the active UI class
     */
    useEffect(() => {
        //01
        document.getElementById(`${window.location.pathname.substring(1)}_nav_item`).classList.add('active_nav_item');
    }, []);

    /**
     * @function (01) get the currently highlighted nav button and remove the highlight
     *           (02) add the highlight to the clicked button
     * @param id of the element where the class is added to
     */
    const changeHighlight = (id) => {
        //01
        document.getElementsByClassName('active_nav_item')[0].classList.remove('active_nav_item')
        //02
        document.getElementById(id).classList.add('active_nav_item');
    }

    /**
     * @function todo
     */
    const showExtView = () => {
        console.log('todo')
    }

    /**
     * @function todo
     */
    const hideExtView = () => {
        console.log('todo')
    }

    return (
            <div className="nav_bar">
                <div className="nav_item">
                    {/*<img src={logo} className="nav_icon large_nav_icon" />*/}
                </div>
                <div className="center_nav_bar">
                    <Link to={"/orders"} id="orders_nav_item" className="nav_item" onClick={() => changeHighlight('orders_nav_item')}>
                        <Tooltip content="Current orders" direction="right">
                            <img src={order} className="nav_icon" />
                        </Tooltip>
                    </Link>
                    <Link to={"/income"} id="income_nav_item" className="nav_item" onClick={() => changeHighlight('income_nav_item')}>
                        <Tooltip content="Revenue" direction="right">
                            <img src={income} className="nav_icon" />
                        </Tooltip>
                    </Link>
                    <Link to={"/promotions"} id="promotions_nav_item" className="nav_item" onClick={() => changeHighlight('promotions_nav_item')}>
                        <Tooltip content="Promotions" direction="right">
                            <img src={promotions} className="nav_icon" />
                        </Tooltip>
                    </Link>
                    <Link to={"/ratings"} id="ratings_nav_item" className="nav_item" onClick={() => changeHighlight('ratings_nav_item')}>
                        <Tooltip content="Ratings" direction="right">
                            <img src={rating} className="nav_icon" />
                        </Tooltip>
                    </Link>
                    <Link to={"/analytics"} id="analytics_nav_item" className="nav_item" onClick={() => changeHighlight('analytics_nav_item')}>
                        <Tooltip content="Analytics" direction="right">
                            <img src={analytics} className="nav_icon" />
                        </Tooltip>
                    </Link>
                    <Link to={"/menu"} id="menu_nav_item" className="nav_item" onClick={() => changeHighlight('menu_nav_item')}>
                        <Tooltip content="Menu" direction="right">
                            <img src={menu} className="nav_icon" />
                        </Tooltip>
                    </Link>
                    <Link to={"/settings"} id="settings_nav_item" className="nav_item" onClick={() => changeHighlight('settings_nav_item')}>
                        <Tooltip content="Settings" direction="right">
                            <img src={settings} className="nav_icon" />
                        </Tooltip>
                    </Link>
                </div>
                <Link to={"/logout"} id="logout_nav_item" className="nav_item" onClick={() => changeHighlight('logout_nav_item')}>
                    <Tooltip content="Logout" direction="right">
                        <img src={logout} className="nav_icon" />
                    </Tooltip>
                </Link>
            </div>
    );
};

export default Navbar;
