import axios from "axios";
import auth_header from "./auth_header";

export const login = (email, password) => {
  return axios
    .post('localhost../login', {email: email, password: password})
    .then((response) => {
      if (response.data.accessToken) localStorage.setItem("user", JSON.stringify(response.data));
      return response.data;
    });
};

export const signup = (employee, restaurant) => {
    return axios
        .post('localhost../login', {employee: employee, restaurant: restaurant})
        .then((response) => {
            if (response.data.accessToken) localStorage.setItem("user", JSON.stringify(response.data));
            return response.data;
        });
};

export const logout = (userType) => {
  return axios
      .post('localhost../logout', {userType}, { headers: auth_header() })
      .then((response) => {
          localStorage.removeItem("user");
          return response.data;
      });
};
