import React, { useState } from "react";
import { Redirect } from 'react-router-dom';

import { useDispatch, useSelector } from "react-redux";

import { history } from "../../helpers/history";

const Signup = () => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [loading, setLoading] = useState(false);

    const dispatch = useDispatch();

    //Change handlers
    const changeEmail = (e) => {
        setEmail(e.target.value);
    };
    const changePassword = (e) => {
        setPassword(e.target.value);
    };

    /**
     * @function (01)
     */
    const performSignup = (e) => {
        e.preventDefault();

        setLoading(true);


        history.push(`/${'menu'}`);
        window.location.reload();


        /*
        dispatch(loginAsync({email: email, password: password}))
            .then(() => {
                props.history.push("/profile");
                window.location.reload();
            })
            .catch(() => {
                setLoading(false);
            });
         */
    };


    return (
        <div className="">
            <form onSubmit={performSignup}>
                <input name="email" className="" placeholder="E-Mail" type="email" required onChange={changeEmail}/>
                <input name="password" className="" placeholder="Password" type="password" required onChange={changePassword}/>
                <button type="submit">
                    {loading && (<span>Processing</span>)}
                    <span>Signup</span>
                </button>
            </form>
        </div>
    );
};

export default Signup;
