import React, { useState, useEffect } from "react";

import {selectMenu, setMenu, selectCurrentCategory} from "../../../slice/menuSlice";
import { useDispatch, useSelector } from "react-redux";

import '../../../styles/sub/menu/MenuCreate.css';

const MenuCreate = ({ hide }) => {
    const menuList = useSelector(selectMenu)
    const categorySelected = useSelector(selectCurrentCategory)

    const [ingredientList, addIngredient] = useState([]);
    const [allergenList, addAllergen] = useState([]);
    const [extraList, addExtra] = useState([]);

    const dispatch = useDispatch();

    /**
     *
     */
    useEffect(() => {
        let bg = document.querySelector('.menu_dark_bg'),
            content = document.querySelector('.menu_input_wrapper');
        bg.style.cssText = "opacity: 100%";
        content.style.marginTop = "0";
    }, [])


    /**
     * @function (01) obtain value of input and check that not empty
     *           (02) use selector to check if category already exists
     *           (03) dispatch to menu reducer and add to data structure
     *           (04) hide the add category view again
     */
    const addMenu = (e) => {
        //01
        e.preventDefault();


        /**
         *
         *
         <input placeholder="Zutaten"/> aus dem usestate extrahieren
         <input placeholder="Allergene"/>
         * aus dem use state extrahieren
         *
         */

        //const val = document.querySelector('.category_input').value;
        //if (!val) return alert('provide value');
        //02
        //if (menuList.filter(category => category.name.toLowerCase() === val.toLowerCase()).length !== 0) return alert('already exists');
        //03 todo set dish to a given category
        //dispatch(setMenu({name: val, rank: 1, dishes:[]}));
        //04
        hideMenuAddView(null, true)
    };

    /**
     * @function (01) get the background and content
     *           (02) return if user clicked on the content
     *           (03) user clicked outside of content - hide
     *           (04) after timeout set state again to hidden
     */
    const hideMenuAddView = (e, forced) => {
        //01
        let bg = document.querySelector('.menu_dark_bg'),
            content = document.querySelector('.menu_input_wrapper');
        //02
        if (!forced && content.contains(e.target)) return;
        //03
        bg.style.cssText = "opacity: 0%";
        content.style.marginTop = "150vh";
        //04
        setTimeout(() => hide(), 350)
    };

    /**
     * @function (01) use the type to get the respective input and its value
     *           (02) check that the value is not empty
     *           (03) perform check if this value already exists in the list
     *           (04) add to the list based on type
     *           (05) clear input value
     * @param type can either be ingredient or allergen
     */
    const addIngredientData = (type) => {
        //01
        let input = document.getElementById(type),
            val = input.value;
        //02
        if (!val) return alert('provide value')
        //03 + 04
        if (type === 'allergen_input') {
            if (allergenList.includes(val)) return alert('ist schon drin')
            addAllergen([...allergenList, val])
        } else {
            if (ingredientList.includes(val)) return alert('ist schon drin')
            addIngredient([...ingredientList, val])
        }
        //05
        input.value = "";
    }
    /**
     * @function
     * @param e
     * @param type
     * @param index
     */
    const removeIngredientData = (e, type, index) => {
        addIngredient([...ingredientList, ingredientList.splice(index, 1)])
    }

    //todo autocomplete ingredients, allergen - if new value occurs add to the list and make it accessible to other people -> im Backend dann schauen ob es das ding schon gibt wenn nicht erstellen und verlinken

    /**
     *
     * @param type
     * @param val
     */
    const addExtraOffer = (desc, price) => {

    }

    /**
     * @function (01) hide the add button
     *           (02) display the uploaded image as background image
     *           (03) show the delete button
     * @param e
     */
    const onChangeMenuImg = (e) => {
        if (!e.target.files[0]) return
        document.getElementById('add_menu_img_icon').style.display = 'none'
        document.getElementById('menu_img_placeholder').style.backgroundImage = "url('"+URL.createObjectURL(e.target.files[0])+"')"
        document.getElementById('remove_menu_img_icon').style.display = 'flex';
    };
    /**
     * @function (01) do not trigger the fiel select
     */
    const deleteMenuImg = (e) => {
        e.currentTarget.style.display = "none"
        document.getElementById('add_menu_img_icon').style.display = 'inline'
        document.getElementById('menu_img_placeholder').style.backgroundImage = "none"
    }

    return (
        <div className="menu_dark_bg" onClick={hideMenuAddView}>
            <div className="menu_input_wrapper">
                <div className="menu_input_hide" onClick={(e) => {hideMenuAddView(e, true)}}>X</div>
                <form className="menu_create_form" onSubmit={addMenu}>

                    <input id="menu_img_create" className="" type="file" name="menu_img" onChange={onChangeMenuImg}/>
                    <label id="menu_img_placeholder" for="menu_img_create">
                        <div id="add_menu_img_icon">Bild hochladen</div>
                        <div id="remove_menu_img_icon" onClick={deleteMenuImg}>-</div>
                    </label>
                    <div id="remove_menu_img_icon" onClick={deleteMenuImg}>-</div>

                    <div className="menu_main_create_wrapper">
                        <input className="menu_create_input" type="text" name="menu_name" placeholder="Name" required/>
                        <textarea className="menu_create_input menu_create_field" type="text" name="menu_desc" placeholder="Beschreibung" required/>
                    </div>

                    <div className="menu_separator"></div>

                    <div className="menu_sub_container_desc">Zutaten</div>
                    <div className="menu_sub_container_desc">Allergene</div>

                    <div className="menu_sub_container">
                        <input id="ingredient_input" className="menu_create_input menu_sub_create_input" type="text" placeholder="Zutat" />
                        <div className="sub_container_add" onClick={() => addIngredientData('ingredient_input')}>Hinzufügen</div>
                        {ingredientList.length > 0 &&
                            ingredientList.map((elem, i) => {
                            return <div className="ingredient_elem" onClick={(e) => removeIngredientData(e, '', i)}>{elem}</div>
                        })
                        }
                    </div>
                    <div className="menu_sub_container">
                        <input id="allergen_input" className="menu_create_input menu_sub_create_input" type="text" placeholder="Allergen" />
                        <div className="sub_container_add" onClick={() => addIngredientData('allergen_input')}>Hinzufügen</div>
                        {allergenList.length > 0 &&
                            allergenList.map((elem, i) => {
                            return <div className="ingredient_elem">
                                <div>{elem}</div>
                                <div className="remove_ingredient_elem" onClick={(e) => removeIngredientData(e, '', i)}>-</div>
                            </div>
                        })
                        }
                    </div>

                    {/*


                        <input type="text" name="menu_desc" placeholder="Vegan"/>
                        <input type="text" name="menu_desc" placeholder="Fleisch -> öffnet dann gar grad"/>
                        <input type="text" name="menu_desc" placeholder="Schärfegrad bestimmen"/>


                        <div className="menu_sub_container">
                        <input placeholder="Extras Liste mit Preis"/>
                        <input type="text" placeholder="Beschreibung" />
                        <input type="text" placeholder="€" />
                        <input type="text" placeholder="ct." />
                        <div onClick={addExtraOffer()}></div>
                    {extraList.map((elem, i) => {
                        return <span>elem</span>
                    })}
                        </div>

                        <input placeholder="Angebot inkl Zeitraum und Preis"/>
                        <input placeholder="€"/>
                        <input placeholder="ct."/>

                        <button className="category_submit">Hinzufügen</button>
                    */}
                </form>
            </div>
        </div>
    );
};

export default MenuCreate;
