import React, {useEffect, useState} from "react";
import { Redirect } from 'react-router-dom';

import { useDispatch, useSelector } from "react-redux";

import { history } from "../../helpers/history";

import "../../styles/Signup.css";

const Signup = () => {

    const [loading, setLoading] = useState(false);
    const [userData, setUserData] = useState({});

    const dispatch = useDispatch();

    useEffect(() => {
     console.log('lit', userData)
    })



    /**
     * @function (01)
     */
    const firstStageSignup = (e) => {
        e.preventDefault();
        setUserData({...userData,  ...Object.fromEntries(new FormData(e.target))})

        let suViews = document.getElementsByClassName('su_section');

        suViews[0].style.marginLeft = '-40vw';
        suViews[1].style.display = 'flex';
        setTimeout(() => {
            suViews[0].style.display = 'none';
            suViews[1].style.marginLeft = '0vw';
        }, 300)
    }

    /**
     * @function (01)
     */
    const prevStageSignup = (e) => {
        let suViews = document.getElementsByClassName('su_section');
        suViews[1].style.marginLeft = '60vw';
        suViews[1].addEventListener('transitionend', moveContent)
        setTimeout(() => {
            suViews[0].style.marginLeft = '0vw';
            suViews[1].removeEventListener(moveContent)
        }, 310)

        const moveContent = () => {
            suViews[1].style.display = 'none';
            suViews[0].style.display = 'flex'
        }
    }

    /**
     * @function (01)
     */
    const performSignup = (e) => {
        e.preventDefault();

        console.log(userData)

        setLoading(true);


        history.push(`/${'menu'}`);
        window.location.reload();


        /*
        dispatch(loginAsync({email: email, password: password}))
            .then(() => {
                props.history.push("/profile");
                window.location.reload();
            })
            .catch(() => {
                setLoading(false);
            });
         */
    };

    return (
        <div className="main_section right_main_section">
            <div id="cta_wrapper">
                <form className="su_section" onSubmit={firstStageSignup}>
                    <input className="signup_input short_input" name="firstName" placeholder="Vorname" type="text" required />
                    <input className="signup_input short_input" name="firstName" placeholder="Nachname" type="text" required />
                    <input className="signup_input" name="email" placeholder="E-Mail" type="email" required />
                    <input className="signup_input" name="password" placeholder="Passwort" type="password" required />
                    <button className="signup_input signup_btn" type="submit">Weiter</button>
                </form>
                <form className="su_section" onSubmit={performSignup}>
                    <input name="restaurantAddress" className="signup_input" placeholder="Restaurant address" type="text" required />
                    <input name="restaurantHouseNo" className="signup_input short_input" placeholder="House number" type="text" required />
                    <input name="restaurantZIP" className="signup_input short_input" placeholder="ZIP" type="text" required />
                    <input name="restaurantName" className="signup_input" placeholder="Restaurant name" type="text" required />
                    <div className="signup_back_btn short_input" onClick={prevStageSignup}>Zurück</div>
                    <button type="submit" className="signup_input signup_btn short_input">
                        {loading && (<span>Processing</span>)}
                        <span>Signup</span>
                    </button>
                    {/*<div id="signup_legal">By clicking Register, you agree to our <a className="signup_link" href='https://werder.de' target='_blank'>Terms</a>. Learn how we collect, use and share your data in our <a className="signup_link" href='https://werder.de' target='_blank'>Data Policy</a> and how we use cookies and similar technology in our <a className="signup_link" href='https://werder.de' target='_blank'>Cookies Policy</a></div>*/}
                </form>
            </div>
        </div>
    );
};

export default Signup;
