import React, { useState } from "react";
import { Redirect } from 'react-router-dom';
import {selectIsLoggedIn, selectMessage, selectUserType, loginAsync} from "../../slice/authSlice";

import { useDispatch, useSelector } from "react-redux";

import { history } from "../../helpers/history";

const Login = () => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [loading, setLoading] = useState(false);

    const isLoggedIn = useSelector(selectIsLoggedIn);
    const message = useSelector(selectMessage);
    const userType = useSelector(selectUserType);

    const dispatch = useDispatch();

    //Change handlers
    const changeEmail = (e) => {
        setEmail(e.target.value);
    };
    const changePassword = (e) => {
        setPassword(e.target.value);
    };

    /**
     * @function (01)
     */
    const performLogin = (e) => {
        e.preventDefault();

        setLoading(true);


        history.push(`/${userType}`);
        window.location.reload();


        /*
        dispatch(loginAsync({email: email, password: password}))
            .then(() => {
                props.history.push("/profile");
                window.location.reload();
            })
            .catch(() => {
                setLoading(false);
            });
         */
    };

    if (isLoggedIn) return <Redirect to="/profile" />;

    return (
        <div className="">
            <form onSubmit={performLogin}>
                <input name="email" className="" placeholder="E-Mail" type="email" required onChange={changeEmail}/>
                <input name="password" className="" placeholder="Password" type="password" required onChange={changePassword}/>
                <button type="submit">
                    {loading && (<span>Processing</span>)}
                    <span>Login</span>
                </button>
            </form>

            {message && (
                <div className="" role="alert">
                    {message}
                </div>
            )}
        </div>
    );
};

export default Login;
