import axios from "axios";


export const validateInviteLink = (hash) => {
    return axios
        .get('http://localhost:3600/invite/validate/'+hash)
        .then((response) => {
            return response.data;
        })
        .catch((e) => {
            console.log('error', e)
        })
};

export const submitInvite = (inviteData) => {
    return axios
        .post('http://localhost:3600/invite/submit/', inviteData)
        .then((response) => {
            if (response.tokenData.accessToken) localStorage.setItem("user", JSON.stringify(response.data.tokenData));
            return response.data;
        })
        .catch((e) => {
            console.log('error', e)
        })
};
