import React, {useEffect, useState, CSSProperties} from "react";
import { Redirect } from 'react-router-dom';
import Select from 'react-select';

import {signUpAsync} from "../../../slice/authSlice";
import { useDispatch, useSelector } from "react-redux";

import { history } from "../../../helpers/history";

import "../../../styles/main/Signup.css";

const Signup = () => {

    const [loading, setLoading] = useState(false);
    const [selectedOption, setSelectedOption] = useState(null);

    const dispatch = useDispatch();


    /**
     * @function (01) prevent the default and update to loading state
     *           (02) dispatch to the signup reducer and wait until fulfilled
     */
    const performSignup = (e) => {
        //01
        e.preventDefault();
        setLoading(true);
        //02
        dispatch(signUpAsync({restaurant: Object.fromEntries(new FormData(e.target))}))
            .then((res) => {
                //history.push("/order");
                //window.location.reload();
            })
            .catch((err) => {
                setLoading(false);
            });
    };

    // =================================

    const options = [
        { value: '00001', label: 'DONAUISAR Klinikum Deggendorf-Dingolfing-Landau' },
        { value: 'rock', label: 'Rock' },
        { value: 'jazz', label: 'Jazz' },
        { value: 'orchestra', label: 'Orchestra' }
    ];

    const customStyles = {
        option: (provided, state) => ({
            ...provided,
            borderBottom: '2px dotted green',
            color: state.isSelected ? 'yellow' : 'black',
            backgroundColor: state.isSelected ? 'green' : 'white',
            width: '100%'
        }),
        control: (provided) => ({
            ...provided,
            margin: "2.75% 0 2.75% 0",
            width: "15vw",
            height: "11.5%",
            backgroundColor: "white",
            padding: "0 2.5% 0 2.5%",
            fontSize: "1em"
        })
    }




    return (
        <div className="main_section right_main_section">
            <div id="cta_wrapper">
                <form className="su_section" onSubmit={performSignup}>
                    <Select
                        styles = { customStyles }
                        options = { options }
                    />
                    <input className="signup_input short_input" name="firstName" placeholder="Vorname" type="text" required />
                    <input className="signup_input short_input" name="lastName" placeholder="Nachname" type="text" required />
                    <input className="signup_input" name="email" placeholder="E-Mail" type="email" required />
                    <input className="signup_input" name="password" placeholder="Passwort" required/>
                    <button type="submit" className="signup_input signup_btn">
                        {loading ? ("In Arbeit ...") : ("Anmelden")}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Signup;
