import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { currentOrders, pastOrders } from '../services/customer.service';

const initialState = {
    currentOrders: [],
    pastOrders: []
};

export const currentOrderAsync = createAsyncThunk(
    'customer/fetchCurrentOrders',
    async () => {
        const response = await currentOrders();
        return response.data;
    }
);

export const pastOrderAsync = createAsyncThunk(
    'customer/fetchPastOrders',
    async () => {
        const response = await pastOrders();
        return response.data;
    }
);

export const customerSlice = createSlice({
    name: 'customer',
    initialState,
    reducers: {
    },
    extraReducers: (builder) => {
        builder
            .addCase(currentOrderAsync.pending, (state) => {
                console.log('loading current orders')
            })
            .addCase(currentOrderAsync.fulfilled, (state, action) => {
                console.log('loaded current orders')
                state.currentOrders = action.payload;
            })
            .addCase(pastOrderAsync.pending, (state) => {
                console.log('loading past orders')
            })
            .addCase(pastOrderAsync.fulfilled, (state, action) => {
                console.log('loaded past orders')
                state.pastOrders = action.payload;
            })
    },
});

export const {} = customerSlice.actions;

export const selectCurrentOrders = (state) => state.customer.currentOrders;
export const selectPastOrders = (state) => state.customer.pastOrders;



export default customerSlice.reducer;
