import React, {useEffect, useState} from "react";
import { Redirect } from 'react-router-dom';

import {signUpAsync} from "../../slice/authSlice";
import { useDispatch, useSelector } from "react-redux";

import { history } from "../../helpers/history";

import "../../styles/Signup.css";

const Signup = () => {

    const [loading, setLoading] = useState(false);
    const [pswVisible, setPswVisibility] = useState(false);
    const [pswBtnVisible, setPswBtnVisibility] = useState(true);
    const [employeeData, setEmployeeData] = useState({});
    const [restaurantData, setRestaurantData] = useState({});

    const dispatch = useDispatch();

    /**
     * @function (01)
     */
    const firstStageSignup = (e) => {
        e.preventDefault();
        setEmployeeData({...employeeData,  ...Object.fromEntries(new FormData(e.target))})

        let suViews = document.getElementsByClassName('su_section');

        suViews[0].style.marginLeft = '-40vw';
        suViews[1].style.display = 'flex';
        setTimeout(() => {
            suViews[0].style.display = 'none';
            suViews[1].style.marginLeft = '0vw';
        }, 300)
    }

    /**
     * @function (01)
     */
    const prevStageSignup = () => {
        let suViews = document.getElementsByClassName('su_section');
        suViews[1].style.marginLeft = '60vw';
        setTimeout(() => {
            suViews[1].style.display = 'none';
            suViews[0].style.display = 'flex';
        }, 300)
        setTimeout(() => {
            suViews[0].style.marginLeft = '0vw';
        }, 310)
    }

    /**
     * @function (01)
     */
    const performSignup = (e) => {
        e.preventDefault();
        setRestaurantData({...restaurantData,  ...Object.fromEntries(new FormData(e.target))})

        setLoading(true);

        //history.push(`/${'menu'}`);
        //window.location.reload();


        dispatch(signUpAsync({employee:employeeData, restaurant:restaurantData}))
            .then(() => {
                history.push("/profile");
                window.location.reload();
            })
            .catch(() => {
                setLoading(false);
            });
    };

    /**
     * @function (01) create a toggle button
     */
    const togglePswVisibility = (e) => {
        let toggleBtn = document.getElementById('pswVisibility');
        toggleBtn.style.cssText = `top:${e.target.offsetTop}px; left:${e.target.offsetLeft}px;`


        toggleBtn.onclick = () => {
            setPswVisibility(!pswVisible)
        }

    }

    return (
        <div className="main_section right_main_section">
            <div id="cta_wrapper">
                <form className="su_section" onSubmit={firstStageSignup}>
                    <input className="signup_input short_input" name="firstName" placeholder="Vorname" type="text" required />
                    <input className="signup_input short_input" name="firstName" placeholder="Nachname" type="text" required />
                    <input className="signup_input" name="email" placeholder="E-Mail" type="email" required />
                    <input className="signup_input" name="password" placeholder="Passwort" type={!pswVisible ? ("password") : ("text")} required/>
                    <button className="signup_input signup_btn" type="submit">Weiter</button>
                    {/*pswBtnVisible && <div id="pswVisibility">hide</div>*/}
                </form>
                <form className="su_section" onSubmit={performSignup}>
                    <input name="restaurantAddress" className="signup_input" placeholder="Restaurant Adresse" type="text" required />
                    <input name="restaurantHouseNo" className="signup_input short_input" placeholder="Hausnummer" type="text" required />
                    <input name="restaurantZIP" className="signup_input short_input" placeholder="Plz" type="text" required />
                    <input name="restaurantName" className="signup_input" placeholder="Restaurant Name" type="text" required />
                    <div className="signup_back_btn short_input" onClick={prevStageSignup}>Zurück</div>
                    <button type="submit" className="signup_input signup_btn short_input">
                        {loading ? ("In Arbeit ...") : ("Anmelden")}
                    </button>
                    <div id="signup_legal">By clicking Register, you agree to our <a className="signup_link" href='https://werder.de' target='_blank'>Terms</a>. Learn how we collect, use and share your data in our <a className="signup_link" href='https://werder.de' target='_blank'>Data Policy</a> and how we use cookies and similar technology in our <a className="signup_link" href='https://werder.de' target='_blank'>Cookies Policy</a></div>
                </form>
            </div>
        </div>
    );
};

export default Signup;
