import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import {login, logout, signup} from '../services/auth.service';

const initialState = {
    loggedIn: localStorage.getItem("loggedIn"),
    userType: localStorage.getItem("userType"),
    userData: {}
};

export const loginAsync = createAsyncThunk(
    'auth/login',
    async (credentials) => {
        return await login(credentials);
    }
);

export const signUpAsync = createAsyncThunk(
    'auth/signup',
    async (signupData) => {
        return await signup(signupData);
    }
);

export const logoutAsync = createAsyncThunk(
    'auth/logout',
    async () => {
        return await logout();
    }
);

export const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        setUserData: (state, action) => {
            //todo update the user data object
            console.log(action.payload)
            state.userData = action.payload;
        }
    },
    extraReducers: (builder) => {
        builder
            .addCase(loginAsync.fulfilled, (state, action) => {
                return {
                    userData: action.payload.userData,
                    loggedIn: true
                }
            })
            .addCase(signUpAsync.fulfilled, (state, action) => {
                console.log('signed up')
                console.log(state)
                console.log('signed up', action.payload)
            })
            .addCase(logoutAsync.fulfilled, (state, action) => {
                console.log('loaded logout');
            });
    },
});

export const {setUserData} = authSlice.actions;

export const selectIsLoggedIn = (state) => state.auth.loggedIn;
export const selectUserData = (state) => state.auth.userData;
export const selectUserType = (state) => state.auth.userType;

export default authSlice.reducer;
