import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import {loadMenu} from '../services/menu.service';

const initialState = {
    menu: [],
    selectedCategory: null
};

export const loadMenuAsync = createAsyncThunk(
    'menu/',
    async () => {
        const response = await loadMenu();
        return response.data;
    }
);

export const menuSlice = createSlice({
    name: 'menu',
    initialState,
    reducers: {
        setMenu: (state, action) => {
            state.menu = [action.payload, ...state.menu]
        },
        setSelectedCategory: (state, action) => {
            state.selectedCategory = action.payload
        }
    },
    extraReducers: (builder) => {
        builder
            .addCase(loadMenuAsync.pending, (state) => {
                console.log('loading');
            })
            .addCase(loadMenuAsync.fulfilled, (state, action) => {
                console.log('loaded');
                console.log('loaded', action.payload)
                //todo assign menu to state
            });
    },
});

export const {setMenu, setSelectedCategory} = menuSlice.actions;

export const selectMenu = (state) => state.menu.menu;
export const selectCurrentCategory = (state) => state.menu.selectedCategory;


export default menuSlice.reducer;
