import React, { useState, useEffect } from "react";

import {selectMenu, setMenu, selectCurrentCategory} from "../../../slice/menuSlice";
import { useDispatch, useSelector } from "react-redux";

import '../../../styles/sub/menu/MenuCreate.css';

const MenuCreate = ({ hide }) => {
    const menuList = useSelector(selectMenu)
    const categorySelected = useSelector(selectCurrentCategory)

    const [ingredientList, addIngredient] = useState([]);
    const [allergenList, addAllergen] = useState([]);
    const [extraList, addExtra] = useState([]);

    const dispatch = useDispatch();

    /**
     *
     */
    useEffect(() => {
        let bg = document.querySelector('.menu_dark_bg'),
            content = document.querySelector('.menu_input_wrapper');
        bg.style.cssText = "opacity: 100%";
        content.style.marginTop = "0";
    }, [])


    /**
     * @function (01) obtain value of input and check that not empty
     *           (02) use selector to check if category already exists
     *           (03) dispatch to menu reducer and add to data structure
     *           (04) hide the add category view again
     */
    const addMenu = (e) => {
        //01


        /**
         *
         *
         <input placeholder="Zutaten"/> aus dem usestate extrahieren
         <input placeholder="Allergene"/>
         * aus dem use state extrahieren
         *
         */

        //const val = document.querySelector('.category_input').value;
        //if (!val) return alert('provide value');
        //02
        //if (menuList.filter(category => category.name.toLowerCase() === val.toLowerCase()).length !== 0) return alert('already exists');
        //03 todo set dish to a given category
        //dispatch(setMenu({name: val, rank: 1, dishes:[]}));
        //04
        hideMenuAddView(null, true)
    };

    /**
     * @function (01) get the background and content
     *           (02) return if user clicked on the content
     *           (03) user clicked outside of content - hide
     *           (04) after timeout set state again to hidden
     */
    const hideMenuAddView = (e, forced) => {
        //01
        let bg = document.querySelector('.menu_dark_bg'),
            content = document.querySelector('.menu_input_wrapper');
        //02
        if (!forced && content.contains(e.target)) return;
        //03
        bg.style.cssText = "opacity: 0%";
        content.style.marginTop = "150vh";
        //04
        setTimeout(() => hide(), 350)
    };

    /**
     * @function (01) use the type to get the respective input and its value
     *           (02) check that the value is not empty
     *           (03) perform check if this value already exists in the list
     *           (04) add to the list based on type
     * @param type can either be ingredient or allergen
     */
    const addIngredientData = (type) => {
        //01
        const val = document.getElementById(type === 'allergen' ? 'allergen_input' : 'ingredient_input')
        //02
        if (!val) return alert('please provide a value')
        //03
        type === 'allergen' ? addAllergen([...allergenList, val]) : addIngredient([...ingredientList, val])

    }

    //todo autocomplete ingredients, allergen - if new value occurs add to the list and make it accessible to other people

    /**
     *
     * @param type
     * @param val
     */
    const addExtraOffer = (desc, price) => {

    }

    return (
        <div className="menu_dark_bg" onClick={hideMenuAddView}>
            <div className="menu_input_wrapper">
                <div className="menu_input_hide" onClick={(e) => {hideMenuAddView(e, true)}}>X</div>
                <form onSubmit={addMenu}>
                    <input type="file" name="menu_img" placeholder="Bild"/>
                    <input type="text" name="menu_name" placeholder="Name"/>
                    <input type="text" name="menu_desc" placeholder="Beschreibung"/>
                    {/*todo liste mit zutaten die nachher dem form hinzugefügt werden*/}
                    <div className="menu_sub_container">
                        <input id="ingredient_input" type="text" placeholder="Zutaten hinzufügen" />
                        <div onClick={addIngredientData()}></div>
                        {ingredientList.map((elem, i) => {
                            return <span>elem</span>
                        })}
                    </div>
                    <div className="menu_sub_container">
                        <input id="allergen_input" type="text" placeholder="Allergene" />
                        <div onClick={addIngredientData()}></div>
                        {allergenList.map((elem, i) => {
                            return <span>elem</span>
                        })}
                    </div>


                    <input type="text" name="menu_desc" placeholder="Vegan"/>
                    <input type="text" name="menu_desc" placeholder="Fleisch -> öffnet dann gar grad"/>
                    <input type="text" name="menu_desc" placeholder="Schärfegrad bestimmen"/>


                    <div className="menu_sub_container">
                        <input placeholder="Extras Liste mit Preis"/>
                        <input type="text" placeholder="Beschreibung" />
                        <input type="text" placeholder="€" />
                        <input type="text" placeholder="ct." />
                        <div onClick={addExtraOffer()}></div>
                        {extraList.map((elem, i) => {
                            return <span>elem</span>
                        })}
                    </div>

                    <input placeholder="Angebot inkl Zeitraum und Preis"/>
                    <input placeholder="€"/>
                    <input placeholder="ct."/>

                    <button className="category_submit">Hinzufügen</button>
                </form>
            </div>
        </div>
    );
};

export default MenuCreate;
