import React, { useState, useEffect } from "react";

import {selectMenu, setSelectedCategory, updateMenuCategory} from "../../../slice/menuSlice";
import {useDispatch, useSelector} from "react-redux";

import '../../../styles/sub/menu/CategoryItem.css';

const CategoryItems = () => {

    const menuList = useSelector(selectMenu)
    const dispatch = useDispatch();


    useEffect(() => {
        console.log(menuList)
    })

    /**
     * @function (01)
     */
    const removeCategory = () => {
        //todo have double check in place where name has to be repeated
    };

    /**
     * @function (01) update the UI of the clicked button and remove the UI from the previously selected button
     *           (02) update the currently selected category
     *           (02) todo update the UI of the category
     * @param index is the index of the currently selected menu category
     */
    const selectCategory = (e, index) => {
        if (document.querySelector('.active_category_item')) document.querySelector('.active_category_item').classList.remove('active_category_item')
        e.currentTarget.classList.add('active_category_item');
        console.log(index)
        dispatch(setSelectedCategory(index));
    }

    /**
     * @function (01)
     */
    const renameCategory = (e, i) => {
        console.log(i)
        let txtField = e.currentTarget.childNodes[0];
        txtField.contentEditable = "true";
        txtField.focus();

        txtField.addEventListener('focusout', () => {
            txtField.contentEditable = "false";
            if (txtField.innerHTML === '') return txtField.innerHTML = menuList[i].name

            //update the menu state
            dispatch(updateMenuCategory({index: i, val:txtField.innerHTML}));

            console.log(menuList)


        })
    }

    //todo drag and drop and change order - https://codepen.io/retrofuturistic/pen/tlbHE

    return (
        <div className="menu_category_container">
            {menuList.length > 0 ? (
                menuList.map((menuCategory, index)=> {
                    return <div className="category_item" draggable="true" onClick={(e) => selectCategory(e, index)} onDoubleClick={(e) => renameCategory(e, index)}>
                        <div className="category_name">{menuCategory.name}</div>
                        {/*<div className="category_dish_count"># {menuCategory.dishes.length}</div>}*/}
                        <div className="category_btn category_rank">{index+1}</div>
                        {/*<div className="category_btn" onClick={removeCategory}>X</div>*/}
                    </div>
                })
            ) : (
                <div className="empty_menu_txt">Erstellen Sie Ihre erste Kategorie</div>
            )}
        </div>
    );
};

export default CategoryItems;
